import { useCallback, useState } from "react"
import { Employee } from "../utils/types"
import { useCustomFetch } from "./useCustomFetch"
import { EmployeeResult } from "./types"

export function useEmployees(): EmployeeResult {
  const { fetchWithCache } = useCustomFetch()
  const [employees, setEmployees] = useState<Employee[] | null>(null)
  const [employeesLoading, setEmployeesLoading] = useState(true) // New loading state for employees

  const fetchAll = useCallback(async () => {
    setEmployeesLoading(true) // Set loading state to true before fetching
    const employeesData = await fetchWithCache<Employee[]>("employees")
    setEmployees(employeesData)
    setEmployeesLoading(false) // Set loading state to false after fetching
  }, [fetchWithCache])



  const invalidateData = useCallback(() => {
    setEmployees(null)
  }, [])

  return { data: employees, loading: employeesLoading, fetchAll, invalidateData } // Return employeesLoading instead of loading
}
