import { useCallback, useState } from "react"
import { RequestByEmployeeParams, Transaction } from "../utils/types"
import { TransactionsByEmployeeResult } from "./types"
import { useCustomFetch } from "./useCustomFetch"

// Add toggleApproval to the TransactionsByEmployeeResult type
export type TransactionsByEmployeeResult = {
  data: Transaction[] | null
  loading: boolean
  fetchById: (employeeId: string) => Promise<void>
  invalidateData: () => void
  toggleApproval: (transactionId: string) => void
}

export function useTransactionsByEmployee(): TransactionsByEmployeeResult {
  const { fetchWithCache, loading } = useCustomFetch()
  const [transactionsByEmployee, setTransactionsByEmployee] = useState<Transaction[] | null>(null)
  // Declare approvalChanges as a Record
  const [approvalChanges, setApprovalChanges] = useState<Record<string, boolean>>({})

  const fetchById = useCallback(
    async (employeeId: string) => {
      const data = await fetchWithCache<Transaction[], RequestByEmployeeParams>(
        "transactionsByEmployee",
        {
          employeeId,
        }
      )

      if (data) { // Check if data is not null
        // Apply approval changes to fetched transactions
        const updatedTransactions = data.map(transaction => ({
          ...transaction,
          approved: approvalChanges[transaction.id] ?? transaction.approved,
        }))

        setTransactionsByEmployee(updatedTransactions)
      }
    },
    [fetchWithCache, approvalChanges]
  )

  const invalidateData = useCallback(() => {
    setTransactionsByEmployee(null)
  }, [])

  // New function for toggling approval
  const toggleApproval = useCallback((transactionId: string) => {
    setApprovalChanges({
      ...approvalChanges,
      [transactionId]: !approvalChanges[transactionId],
    })
  }, [approvalChanges])

  return { data: transactionsByEmployee, loading, fetchById, invalidateData, toggleApproval } // Return toggleApproval
}
